<template>
  <div class="box">
    <transition name="mint-toast-pop">
      <div class="mint-toast" v-show="visible" :class="customClass" :style="{ 'padding': '8px' }">
        <i class="loading-icon"></i>
        <span class="mint-toast-text" v-show="message">{{ message }}</span>
      </div>
    </transition>
  </div>
</template>

<script type="text/babel">
export default {
  props: {
    message: String,
    className: {
      type: String,
      default: ''
    },
    position: {
      type: String,
      default: 'middle'
    },
  },
  data () {
    return {
      visible: false
    };
  },
  computed: {
    customClass () {
      var classes = [];
      switch (this.position) {
        case 'top':
          classes.push('is-placetop');
          break;
        case 'bottom':
          classes.push('is-placebottom');
          break;
        default:
          classes.push('is-placemiddle');
      }
      classes.push(this.className);
      return classes.join(' ');
    }
  }
};
</script>

